#ifndef __BIG_LOCK_H__
#define __BIG_LOCK_H__

extern os_mutex_t biglock;

static inline int big_lock(unsigned int timeout)
{
	return os_mutex_get(&biglock,
				timeout);
}

static inline int big_unlock()
{
	return os_mutex_put(&biglock);
}

#endif /* __BIG_LOCK_H__ */
